mapper.select:
select id, name, salary, status, phone, password from user
where 1=1

    and id = #{id}


    and name = #{name}


    and salary = #{salary}


    and status = #{status}


    and phone = #{phone}


    and password = #{password}

mapper.insert:
insert into user(
id, name, salary, status, phone, password
) values(
  #{id}, #{name}, #{salary}, #{status}, #{phone}, #{password}
)
